import React, { useState, useEffect } from 'react';
import {
  Container,
  Card,
  Form,
  Button,
  Row,
  Col
} from 'react-bootstrap';

import { useMutation } from '@apollo/client';
import { LOGIN_USER } from '../utils/mutations';
import Auth from '../utils/auth';

const styles = {
  Text: {
  fontFamily: 'Orbitron',
  fontWeight: 'bold',
  color: 'white',
  textShadow: '2px 2px #000000'

},
  Button: {
    color: 'white',
    backgroundColor: '#0074FF',
    borderRadius: '6px'
  },
  Card: {
    textAlign: 'center',
    justifyContent: 'center',
    alignItems: 'center',
    background: 'linear-gradient(90deg, #FC466B 0%, #3F5EFB 100%)'
  }
}


const LForm = () => {

  const [userFormData, setUserFormData] = useState({ username: '', password: '' });

  const [validated] = useState(false);

  const [showAlert, setShowAlert] = useState(false);

  const [login, {error}] = useMutation(LOGIN_USER);

  useEffect(() => {
    if(error) {
      setShowAlert(true);
    }else {
      setShowAlert(false);
    }
  }, [error]);

  //login
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setUserFormData({ ...userFormData, [name]: value });
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();

    // check if form has everything (as per react-bootstrap docs)
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }

    try {
        console.log({...userFormData})
      const { data } = await login ({
        variables: { ...userFormData },
      });

      console.log(data);
      Auth.login(data.login.token);
    } catch (err) {
      console.error(err);
    }

    //clears form values
    setUserFormData({
      username: '',
      email: '',
      password: '',
    });
  };



  return (
    <>
      <Container>
        <Card style={styles.Card}>
          <h1 style={styles.Text}>Konnect</h1>
          <Form>
            <Row>
              <Col>
                <Form.Label htmlFor='username' style={styles.Text}>Username:</Form.Label>
              </Col>
              <Col>
                <Form.Control
                  type='text'
                  placeholder=''
                  name='username'
                  onChange={handleInputChange}
                  value={userFormData.username}
                  required
                />
              </Col>
            </Row>

            <Row>
              <Col>
                <Form.Label htmlFor='password' style={styles.Text}>Password:</Form.Label>
              </Col>
              <Col>
                <Form.Control
                  type='text'
                  placeholder=''
                  name='password'
                  onChange={handleInputChange}
                  value={userFormData.password}
                  required
                />
              </Col>
            </Row>
          </Form>
          <Row>

            <Col>
            <a
              href='/login'
              onClick={() => handlePageChange ('Login')}>

              <Button style={styles.Button}>Login</Button>
              </a>
            </Col>
          </Row>
          <Row>
            Don't Have an Account?
          </Row>
          <Row>
            <Button style={styles.Button}>Sign Up Here!</Button>
          </Row>

        </Card>

      </Container>
    </>
  )
}

export default LForm;
